import React, { Component } from "react";
import "./Results.css";
import { connect } from "react-redux";
import UserStore from "../../store/user/UserStore";
import PaperSheet from "../../components/UI/Paper";
import SimpleExpansionPanel from "../../components/UI/ExpansionPanel";

class ViewResults extends Component {

  componentWillMount = () => {
    if(this.props.location.state) {
      this.props.getPatient(this.props.location.patientId);
    }
  }
  componentDidMount = () => {
    // if(this.props.location.state) {
    //   this.props.getPatient(this.props.location.patientId);
    // }
    console.log(this.props.patient);
  }
  render() {
    return (
      <PaperSheet>
        <div className='ViewResultsContainer'>
          {this.props.patient_stats ? this.props.patient_stats.map(result => (
            <SimpleExpansionPanel title={result.label} value={result.value} className="resultPanel" />
          )) : <h2>No Previous Results</h2>}
        </div>
      </PaperSheet>
    );
  }
}

const mapStateToProps = state => {
  return {
    patient: state.loadedPatient,
    patient_stats: [
      { label: "Heart Rate", value: "89 BPM" },
      { label: "Blood Pressure", value: "120 / 80" }
    ]
  };
};

const mapDispatchToProps = dispatch => {
  return {
    addResults: (id, results) => {
      dispatch(UserStore.getPatient(id));
    },
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ViewResults);
