import * as actionTypes from './action';

const initState = {
    user: {
        isAuthenticated: false,
        user: null
    },
}

export function user(state = initState, action) {
    console.log(action);
    switch (action.type) {
        case actionTypes.LOGIN:
            return {
                ...state,
                isAuthenticated: true,
                user: action.user.data
            }
        default: 
            return state;
        }
}
