import React, { Component } from "react";
import "./Landing.css";
import {connect} from 'react-redux';
import UserStore from "../../store/user/UserStore";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";

class EmployeeLanding extends Component {
  state = {
    firstName: "David",
    lastName: "Trafford",
    designation: 'MD',
    title: ''
  };

  componentDidMount = () => {
    // let title = '';
    // switch (this.state.designation) {
    //   case 'MD':
    //     this.setState({ title: 'Doctor' });
    //     break;
    //   case 'RPN':
    //     this.setState({ title: 'Nurse' });
    //     break;
    //   case 'RN':
    //     this.setState({ title: 'Nurse' });
    //     break;
    //   default: 
    //     return;
    //   }
  }
  render() {
    return (
      <div className='landingContainer'>
        <h1>
          Welcome - {this.props.user.firstName} {this.props.user.lastName} ({this.props.user.designation})
        </h1>
        <hr/>
        <ul>
          <li>
            <Link to={"/add_tip"} style={{ marginLeft: "auto" }}>
              <button onClick={""} type='button' class='landingLinkButton'>
                ADD A TIP FOR PATIENT
              </button>
            </Link>
          </li>
          <li>
            <Link to={"/view_results"} style={{ marginLeft: "auto" }}>
              <button onClick={""} type='button' class='landingLinkButton'>
                VIEW PATIENT RESULTS
              </button>
            </Link>
          </li>
          <li>
            <Link to={"/update_patient"} style={{ marginLeft: "auto" }}>
              <button onClick={""} type='button' class='landingLinkButton'>
                UPDATE INFO
              </button>
            </Link>
          </li>
        </ul>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
  };
};

const mapDispatchToProps = dispatch => {
  return {
    patientLogIn: patient => {
      dispatch(UserStore.patientLogIn(patient));
    },
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(EmployeeLanding);
