///////////*****************//////// USERSTORE

import { apiUrl } from "../../instances/instance";
import { logIn } from "./function";

export default class UserStore {
  // pull data /////
  static patientLogIn = id => {
    return dispatch => {
      apiUrl
        .post("/patient/login", { crossdomain: true })
        .then(patient => {
          dispatch(logIn(patient));
        })
        .catch(err => {
          console.log(err);
        });
    };
  };
