import React, { Component } from 'react'
import './DiscussionBoard.css';
import {connect} from 'react-redux';
import UserStore from "../../store/user/UserStore";
import PaperSheet from "../../components/UI/Paper";
import Button from '@material-ui/core/Button';
import PostExpansionPanel from "../../components/UI/PostExpansionPanel";
import { stat } from "fs";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";

class DiscussionBoard extends Component {
  constructor() {
    super()
    this.state = {
      posts: null
    }
  }
    render() {
        return (
            <PaperSheet>
              {/* TODO: USE THIS FOR REDIRECT ON ALL PAGES IF NOT LOGGED IN */}
            {this.props.userType === "EM" ? 
              <Link to={'/add_tip'} className="addTip_Button"><Button variant="contained" color="primary">Add Tip</Button></Link>
             : null}
            <div className='ViewResultsContainer'>
              {this.props.posts ? this.props.posts.map(post => (
                <PostExpansionPanel title={post.title} message={post.message} delete={this.props.userType === "EM" ? () => this.deleteTip(post) : null} className="resultPanel" />
              )) : <h2>No Posts Yet!</h2>}
            </div>
          </PaperSheet>
        )
    }
}


const mapStateToProps = state => {
  return {
    userType: state.userType,
    health_tips: state.healthTips
  };
};

const mapDispatchToProps = dispatch => {
  return {
    deleteTip: (id) => {
      dispatch(UserStore.deleteTip(id));
    },
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(DiscussionBoard);
